mapper.select:

select no, name, email, gender
  from member 
 where no=#{no }


select no, name 
  from member 
 where email=#{email }

mapper.update:

	update member 
	   set name=#{name },


				   
	  	   password=#{password },
	  	   
	   	   
	   	   
	       gender=#{gender }
	 where no=#{no }
